    >
      <div>


      </div>
    </ReactDependentScript> */}


{/* <Script
      url = "http://localhost:3000/active.js"

    /> */}

      <SelectDrop
        value={selectedOption}
        onChange={this.handleChange}
        options={options}
        name={'candy'}
        // styles={customStyles}
        theme={theme => ({
          ...theme,
          borderRadius: 0,
          colors: {
            ...theme.colors,
            primary25: '#efefef',
            primary50: '#efefef',
            primary: '#ccc'
          },
        })}
      />


      <SelectDrop
        value={selectedOption2}
        onChange={this.handleChange2}
        options={options2}
        name={'candy2'}
        // styles={customStyles}
        theme={theme => ({
          ...theme,
          borderRadius: 0,
          colors: {
            ...theme.colors,
            primary25: '#efefef',
            primary50: '#efefef',
            primary: '#ccc'
          },
        })}
      />

      </>
    );
  }
}

export default App;
