import React from 'react';
import SelectDrop from 'react-select';
import sampleData from '../src/sample.js';

// import ReactDependentScript from 'react-dependent-script';


import Script from 'react-load-script'




// import * as utils from '../src/pristine'; 


const subsampleData = [
  { value: 'chocolate2', label: 'Chocolate2' },
  { value: 'strawberry2', label: 'Strawberry2' },
  { value: 'vanilla2', label: 'Vanilla2' },
  { value: 'chocolate2', label: 'Chocolate2' }
]

const subsampleDataini = [
  { value: 'chocolate2', label: 'Orange' }
]



const options = sampleData;
let options2 = subsampleDataini;


const customStyles = {
  option: (provided, state) => ({
    ...provided,
    // borderBottom: '1px dotted pink',
    color: state.isSelected ? 'red' : 'blue',
    padding: 20,
  }),
  control: () => ({
    // none of react-select's styles are passed to <Control />
    //width: 200,
  }),
  singleValue: (provided, state) => {
    // const opacity = state.isDisabled ? 0.5 : 1;
    // const transition = 'opacity 300ms';

    // return { ...provided, opacity, transition };
  }
}




class App extends React.Component {

  componentDidMount(){



  }
  state = {
    selectedOption: null,
    selectedOption2: null,
  };
  handleChange = selectedOption => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
    console.log(selectedOption.value);
    if(selectedOption.value==='chocolate'){
      options2 = subsampleData;
    }else{
      options2 = subsampleDataini;
    }

  };

  handleChange2 = selectedOption2 => {
    this.setState({ selectedOption2 });
    console.log(`Option selected:`, selectedOption2);
    console.log(selectedOption2.value);
  };
  render() {
    const { selectedOption } = this.state;
    const { selectedOption2 } = this.state;

    // utils.myfunc();

    return (
      <>

    {/* <ReactDependentScript
      loadingComponent={<div>jQuery is loading...</div>}
      scripts={['https://lendified-demo.netlify.com/assets/js/pristine.js','https://lendified-demo.netlify.com/assets/js/functions.js']}
    >
      <div>


      </div>
    </ReactDependentScript> */}


    <form autocomplete="off" class="form" role="form" id="form">
                  <div class="show-helper">           

                    <div class="form-group">
                      <input class="form-control extra-rounded" id="full-name" required="required" title="Full Name" type="text" placeholder="Full Name" />
                      <i class="fa fa-user-o form-icon" aria-hidden="true"></i>
                      <label for="address" class="ml-2">Avoid nicknames</label> 
                    </div>



                  </div>


                  <div class="form-group row">

                    <div class="col-md-12 mt-4">
                      <div class="block-container">
                          <button id="submit" class="btn btn-success btn-lg btn-block shadow" type="submit">Generate Link</button>

                      </div>
                    </div>
                  </div>
                </form>

      <SelectDrop
        value={selectedOption}
        onChange={this.handleChange}
        options={options}
        name={'candy'}
        // styles={customStyles}
        theme={theme => ({
          ...theme,
          borderRadius: 0,
          colors: {
            ...theme.colors,
            primary25: '#efefef',
            primary50: '#efefef',
            primary: '#ccc'
          },
        })}
      />


      <SelectDrop
        value={selectedOption2}
        onChange={this.handleChange2}
        options={options2}
        name={'candy2'}
        // styles={customStyles}
        theme={theme => ({
          ...theme,
          borderRadius: 0,
          colors: {
            ...theme.colors,
            primary25: '#efefef',
            primary50: '#efefef',
            primary: '#ccc'
          },
        })}
      />

      </>
    );
  }
}

export default App;
